import React, {useState} from "react";
import axios from "axios";
import Track from "./Track";
import './Playlist.css'

function Playlist(props) {
    const {playlist, removeFromPlaylist, token} = props;

    const [playlistName, setPlayListName] = useState('')
    const [playlists, setPlaylists] = useState([])
    const uriArray = [];

    async function handleSubmit(e) {
        e.preventDefault();
        if(playlistName === ''){
            alert('Must have a playlist name!')
        }
        else if(playlist.length === 0){
            alert('No tracks in playlist')
        }
        else if(!token){
            alert('You need to be logged in to upload playlists to Spotify')
        }
        else{
            //get user id
            const responseUserId = await axios.get ('https://api.spotify.com/v1/me', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            const USER_ID = responseUserId.data.id;
            //Get track uris
            for (let i = 0; i < playlist.length; i++){
                uriArray.push(playlist[i].uri)
            }
            const uriToPost = JSON.stringify(uriArray);
            console.log(uriToPost)
            //Upload playlist to Spotify
            await axios({
                method: 'post',
                url: `https://api.spotify.com/v1/users/${USER_ID}/playlists`,
                headers: {'Authorization': `Bearer ${token}`},
                data: {
                    'name': `${playlistName}`,
                    'description': 'Playlist created with JAMMMING app',
                    'public': false
                }
            })
            .then(async function (response) {
                //post tracks
                const url = await axios.get (`https://api.spotify.com/v1/playlists/${response.data.id}/tracks`, {
                    headers: {Authorization: `Bearer ${token}`}
                });
                const URL = url.config.url
                console.log(URL)
                await axios({
                    method: 'post',
                    url: `${URL}`,
                    headers: {'Authorization' : `Bearer ${token}`},
                    data: {
                        "uris" : "[`${uriToPost}`]", 'postition' : 0
                    }
                })
                .then(function (response) {
                    console.log(response)  
                    //reset
                    alert(`Upload ${playlistName} successful`)
                    setPlaylists(playlists => [...playlists, playlistName])
                    setPlayListName('')
                    playlist.splice(0)
                })
                .catch(function (error) {
                    if(error.response){
                        console.log(error.response)
                    }
                    else if(error.request){
                        console.log(error.request)
                    }
                    else if(error.message){
                        console.log(error.message)
                    }
                    else{
                        console.log('Good luck')
                    }
                })  
            })
            .catch(function (error) {
                console.log(error);
                alert(error)
            });
        }
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input 
                    className='heading'
                    onChange={(e) => setPlayListName(e.target.value)}
                    value={playlistName}
                />
                <ul>
                    {playlist?.map((track) => (
                        <Track
                            trackIsInPlaylist = {true}
                            key={track.id}
                            track={track}
                            removeFromPlaylist={() => {removeFromPlaylist(track)}} 
                        />
                    ))}
                </ul>
                <button className='heading' type="submit">
                    Upload My Jammms!
                </button>
            </form>
            {/*add functionality to be able to change playlist names*/}
            <h3 className='heading'>Saved Playlists</h3>
            <ul>
                {playlists?.map((pl) => (
                    <li>
                        {pl}
                    </li>
                ))}
            </ul>

        </>
    )
}

export default Playlist;
