import React, {useState} from "react";
import axios from "axios";
import Track from "./Track";
import './Playlist.css'

function Playlist(props) {
    const {playlist, removeFromPlaylist, token} = props;

    const [playlistName, setPlayListName] = useState('')
    const [playlists, setPlaylists] = useState([])
    const newUriPlaylist = [];

    async function handleSubmit(e) {
        e.preventDefault();
        if(playlistName === ''){
            alert('Must have a playlist name!')
        }
        else if(playlist.length === 0){
            alert('No tracks in playlist')
        }
        else if(!token){
            alert('You need to be logged in to upload playlists to Spotify')
        }
        else{
            //get user id
            const responseUserId = await axios.get ('https://api.spotify.com/v1/me', {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            const USER_ID = responseUserId.data.id;

            //Upload playlist to Spotify
            await axios({
                method: 'post',
                url: `https://api.spotify.com/v1/users/${USER_ID}/playlists`,
                headers: {'Authorization': `Bearer ${token}`},
                data: {
                    'name': `${playlistName}`,
                    'description': 'Playlist created with JAMMMING app',
                    'public': false
                }
            })
            .then(async function (response) {
                console.log(response.data.id);                
              })
              .catch(function (error) {
                console.log(error);
                alert(error)
              });

            /*//POST tracks to playlist
            //get playlist id
            const responsePlaylistId = await axios.get ('', {
                headers: {Authorization: `Bearer ${token}`}
            });
            const PLAYLIST_ID = responsePlaylistId;
            //Get track URI
            for (let i = 0; i < playlist.length; i++){
                const playlistUri = playlist[i].uri;
                newUriPlaylist.push(playlistUri)
            }
            //post tracks
            await axios({
                method: 'post',
                url: `https://api.spotify.com/v1/playlists/${PLAYLIST_ID}/tracks`,
                headers: {'Authorization' : `Bearer${token}`},
                uris: [`${newUriPlaylist}`]
            })
            .then(function (response) {
                console.log(response)
            })
            .catch(function (error) {
                console.log(error)
            })*/
            //reset
            alert(`Upload ${playlistName} successful`)
            setPlaylists(playlists => [...playlists, playlistName])
            setPlayListName('')
            playlist.splice(0)
            newUriPlaylist.splice(0)
        }
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input 
                    class='heading'
                    onChange={(e) => setPlayListName(e.target.value)}
                    value={playlistName}
                />
                <ul>
                    {playlist?.map((track) => (
                        <Track
                            trackIsInPlaylist = {true}
                            key={track.id}
                            track={track}
                            removeFromPlaylist={() => {removeFromPlaylist(track)}} 
                        />
                    ))}
                </ul>
                <button class='heading' type="submit">
                    Upload My Jammms!
                </button>
            </form>
            {/*add functionality to be able to change playlist names*/}
            <h3 class='heading'>Saved Playlists</h3>
            <ul>
                {playlists?.map((pl) => (
                    <li>
                        {pl}
                    </li>
                ))}
            </ul>

        </>
    )
}

export default Playlist;
