import React, {useState} from 'react'
import Tracklist from './Tracklist';
import Playlist from './Playlist';

function SearchResults(props) {
    const {searchResults} = props;
    console.log(searchResults)


    const renderTrack = () => {
        return searchResults?.map(track => (
            <div key={(track.id)}>
                <ul>
                    <li>{track.id}</li>
                    <li>{track.name}</li>
                    <li>{track.artists.map(artist => (
                        artist.name
                    ))}</li>
                    <li>{track.album.name}</li>
                </ul>
                <button onClick={addToPlaylist}>+</button>        
            </div>
        ))
    }

    const [playlist, setPlaylist] = useState([]);

    const addToPlaylist = (track) => {
        let playlistChecker = track.name;
        playlist.find((track) => track.name === playlistChecker)
        if(playlist.find((track) => track.name === playlistChecker)){
            alert(track.name + ' is already in playlist!')
        }
        else{
            let newId = track.id+1; /*Replace me with .utilities generate function when written*/
            const newTrack = {
                songName: track.songName,
                artist: track.artist,
                album: track.album,
                id: newId,
                playlist: true
            }
            setPlaylist(playlist => [...playlist, newTrack])
        }
    }
    const removeFromPlaylist = (trackToRemove) => {
        setPlaylist((playlist) => 
            playlist.filter((track) => track.id !== trackToRemove.id))
    }

    return (
        <>
            <div>
                <h3>Search Results</h3>
                {renderTrack()}
                <Tracklist 
                    searchResults={searchResults}
                    addToPlaylist={addToPlaylist}
                />
            </div>
            <div>
                <h3>Playlist</h3>
                <Playlist
                    playlist={playlist}
                    removeFromPlaylist={removeFromPlaylist}
                />
            </div>

        </>
    )
}

export default SearchResults;
