import React, {useState} from "react";

function Track(props) {
    const {track, addToPlaylist, removeFromPlaylist, labelVariable} = props;

    const [isPlaylist, setIsPlaylist] = useState(false);

    const handleClick = () => {
        if(isPlaylist){
            removeFromPlaylist(track.id);
        }
        else{
            addToPlaylist(track.id);
        }
    }

    return(
        <li className="Track">
            <div className="songName">{track.name}</div>
            <div className="songInfo">{track.artist}+' '+{track.album}</div>
            <button 
                type="button"
                className="playlistAddRemove"
                aria-label={labelVariable}
                onClick={handleClick}>
                    {isPlaylist ? '-' : '+'}
            </button>
        </li>
    )
}

export default Track;
