import React, {useState} from "react";
import Track from "./Track";

function Tracklist() {

    const [tracklist, setTracklist] = useState([{
        'name' : 'Something For Nothing',
        'artist' : 'Rush', 
        'album' : '2112',
        'id' : '1', /*Replace me with .utilities generate function when written*/
        'playlist' : false 
    }]);

    const [playlist, setPlaylist] = useState([]);

    const addToPlaylist = (track) => {
        if(playlist.filter((track) => track.id == track)){
            alert(`$(track.name) is already in your playlist`);
        }
        else{
            track.playlist = true;
            setPlaylist(playlist => [...playlist, track])
        }
    }

    const removeFromPlaylist = (trackToRemove) => {
        setPlaylist((playlist) => playlist.filter((track) => track.id !== trackToRemove))
    }

    return (
        <>
            <ul>
                {tracklist.map((track) => (
                    <Track 
                        key={track.id}
                        track={track}
                        addToPlaylist={addToPlaylist}
                        removeFromPlaylist={removeFromPlaylist} />
                )
                )}
            </ul>
        </>

    )
}

export default Tracklist;
