import React, {useState} from "react";
import Track from "./Track";

function Playlist(props) {
    const {playlist, removeFromPlaylist} = props;

    const [playlistName, setPlayListName] = useState('')
    const [playlists, setPlaylists] = useState('')

    function handleSubmit(e) {
        e.preventDefault();
        if(playlistName === ''){
            alert('Must have a playlist name!')
        }
        else if(playlist.length === 0){
            alert('No tracks in playlist')
        }
        else{
            //Save the new playlist
            setPlaylists(playlists => [...playlists, playlist])
            alert(`Upload ${playlistName} successful`)
            setPlayListName('')
            playlist.splice(0)
        }
    }

    return(
        <>
            <form onSubmit={handleSubmit}>
                <input 
                    onChange={(e) => setPlayListName(e.target.value)}
                    value={playlistName}
                />
                <ul>
                    {playlist?.map((track) => (
                        <Track
                            key={track.id}
                            track={track}
                            removeFromPlaylist={() => {removeFromPlaylist(track)}} 
                        />
                    ))}
                </ul>
                <button type="submit">
                    Upload My Jammms!
                </button>
            </form>
            <p>
                {playlists}
            </p>
        </>
    )
}

export default Playlist;
